mapper.select:
SELECT c.id, c.class_no, c.class_name, c.grade_id, g.grade_name, c.head_teacher_id, t.teacher_name as head_teacher_name, c.classroom, c.max_students, c.current_students, c.school_year, c.status, c.remark, c.created_at, c.updated_at FROM edu_class c LEFT JOIN edu_grade g ON c.grade_id = g.id AND g.is_deleted = 0 LEFT JOIN edu_teacher t ON c.head_teacher_id = t.id AND t.is_deleted = 0 WHERE c.is_deleted = 0
AND c.class_name LIKE CONCAT('%', #{className}, '%')
AND c.grade_id = #{gradeId}
AND c.head_teacher_id = #{headTeacherId}
AND c.school_year = #{schoolYear}
AND c.status = #{status}
ORDER BY c.created_at DESC

mapper.update:
UPDATE edu_class
SET current_students = current_students + #{count}
WHERE id = #{classId} AND is_deleted = 0
